	public function getJsonData($dataStore)
	{
		$storeToFilenameMap = array(
			'getAllContacts' 	=> 'Contacts.json',
			'getItems' 				=> 'Items.json',
			'getAllAccounts'	=> 'Accounts.json'
		);
		
		$file = $storeToFilenameMap[$dataStore];
		
		$path = DRUPAL_ROOT .'/sites/default/files/uploads/json/'.$file;
		// print $path;exit;
		return file_get_contents($path);
	}
	
